       INTO TBL_ORDER
(
  ORDER_NO
, CORP_NO
, PROD_NO
, USER_NO
, ORDER_PRICE
, DEALING_STATUS
, ORDER_STATUS
, ORDER_QUANTITY
, ORDER_DATE
) 
VALUES 
(
  'ORDR' || LPAD(SEQ_ORDER.NEXTVAL,4,0)
, #{corpNo}
, #{prodNo}
, #{userNo}
, #{orderPrice}
, #{dealingStatus}
, #{orderStatus}
, #{orderOuantity}
, SYSDATE
)

