SELECT COUNT(*) FROM TBL_ORDER A JOIN TBL_CORP_USER B ON(A.CORP_NO = B.CORP_NO) JOIN TBL_PRODUCT C ON(A.PROD_NO = C.PROD_NO) JOIN TBL_PROD_CATEGORY D ON(C.PROD_CTG_NO = D.PROD_CTG_NO) JOIN TBL_USER E ON(A.USER_NO = E.USER_NO) WHERE A.USER_NO = #{ no }
AND A.ORDER_DATE >= #{ search.searchWriteDateStart }
AND A.ORDER_DATE <= #{ search.searchWriteDateEnd }
AND A.ORDER_DATE BETWEEN #{ search.searchWriteDateStart } AND #{ search.searchWriteDateEnd }
AND D.PROD_CTG_NO = #{ search.largeSearchCondition }
C.PROD_NAME LIKE '%' || #{ search.searchValue } || '%'
C.PROD_NO LIKE '%' || #{ search.searchValue } || '%'
A.ORDER_NO LIKE '%' || #{ search.searchValue } || '%'
B.CORP_ID LIKE '%' || #{ search.searchValue } || '%'
B.CORP_NAME LIKE '%' || #{ search.searchValue } || '%'

SELECT A.RNUM , A.ORDER_NO , A.CORP_ID , A.CORP_NAME , A.PROD_NAME , A.PROD_CTG_NAME , A.USER_NO , A.ORDER_PRICE , A.ORDER_DATE , A.DEALING_STATUS FROM ( SELECT ROWNUM RNUM , B.ORDER_NO , B.CORP_ID , B.CORP_NAME , B.PROD_NAME , B.PROD_CTG_NAME , B.USER_NO , B.ORDER_PRICE , B.ORDER_DATE , B.DEALING_STATUS FROM ( SELECT C.ORDER_NO , D.CORP_ID , D.CORP_NAME , E.PROD_NAME , F.PROD_CTG_NAME , G.USER_NO , C.ORDER_PRICE , C.ORDER_DATE , C.DEALING_STATUS FROM TBL_ORDER C JOIN TBL_CORP_USER D ON(C.CORP_NO = D.CORP_NO) JOIN TBL_PRODUCT E ON(C.PROD_NO = E.PROD_NO) JOIN TBL_PROD_CATEGORY F ON(E.PROD_CTG_NO = F.PROD_CTG_NO) JOIN TBL_USER G ON(C.USER_NO = G.USER_NO) WHERE C.USER_NO = #{ no } ORDER BY C.ORDER_NO DESC ) B
AND B.ORDER_DATE >= #{ search.searchWriteDateStart }
AND B.ORDER_DATE <= #{ search.searchWriteDateEnd }
AND B.ORDER_DATE BETWEEN #{ search.searchWriteDateStart } AND #{ search.searchWriteDateEnd }
AND B.PROD_CTG_NO = #{ search.largeSearchCondition }
AND B.PROD_NAME LIKE '%' || #{ search.searchValue } || '%'
AND B.PROD_NO LIKE '%' || #{ search.searchValue } || '%'
AND B.ORDER_NO LIKE '%' || #{ search.searchValue } || '%'
AND B.CORP_ID LIKE '%' || #{ search.searchValue } || '%'
AND B.CORP_NAME LIKE '%' || #{ search.searchValue } || '%'
) A WHERE A.RNUM BETWEEN #{ search.pageInfo.startRow } AND #{ search.pageInfo.endRow }
